/*!40000 ALTER TABLE `blog_art` DISABLE KEYS */;
INSERT INTO `blog_art` VALUES (1,'LNMP环境的搭建之nginx篇','&lt;p&gt;&lt;span style=&quot;font-size: 20px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;strong&gt;1. 操作系统&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;我选择了一款国外的VPS来搭建LNMP环境，操作系统：Centos 6 x86 minimal 。&lt;/span&gt;&lt;p&gt;&lt;span style=&quot;font-size: 20px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 16px;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 20px;&quot;&gt;2. 安装nginx&lt;/span&gt;&lt;/strong&gt;&lt;strong&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 20px;&quot;&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;Centos 6 默认软件源中没有nginx，参考&lt;a href=&quot;http://nginx.org/en/linux_packages.html#stable&quot; target=&quot;_blank&quot; title=&quot;nginx官方介绍&quot;&gt;nginx官方介绍&lt;/a&gt;添加软件源：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: center;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;img src=&quot;/upload/image/20170224/1487908522726620.png&quot; title=&quot;1487908522726620.png&quot; alt=&quot;11.PNG&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;然后执行：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:bash;toolbar:false&quot;&gt;sudo&amp;nbsp;yum&amp;nbsp;-y&amp;nbsp;install&amp;nbsp;nginx&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 20px;&quot;&gt;3. 设置开机自启动&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;pre class=&quot;brush:bash;toolbar:false&quot;&gt;sudo&amp;nbsp;chkconfig&amp;nbsp;nginx&amp;nbsp;on&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 20px;&quot;&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;','2017-02-24',1,13,1),(2,'LNMP环境的搭建之mysql篇','&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 20px;&quot;&gt;1. 安装mysql-server&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;centos 6 默认软件源中已存在mysql-server软件包，无需添加额外软件源，只执行安装命令即可。&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;pre class=&quot;brush:bash;toolbar:false&quot;&gt;sudo&amp;nbsp;yum&amp;nbsp;-y&amp;nbsp;install&amp;nbsp;mysql-server&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 20px;&quot;&gt;2. 设置开机启动&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:bash;toolbar:false&quot;&gt;sudo&amp;nbsp;chkconfig&amp;nbsp;mysqld&amp;nbsp;on&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 20px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 20px;&quot;&gt;3.&amp;nbsp;&lt;/span&gt;启动MySql服务&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:bash;toolbar:false&quot;&gt;sudo&amp;nbsp;service&amp;nbsp;mysqld&amp;nbsp;start&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 20px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;strong&gt;4.&amp;nbsp;设置MySQL的root用户设置密码&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:bash;toolbar:false&quot;&gt;mysql&amp;nbsp;-u&amp;nbsp;root\r\nmysql&amp;gt;&amp;nbsp;set&amp;nbsp;password&amp;nbsp;for&amp;nbsp;root@localhost=password(&amp;#39;root&amp;#39;);\r\nmysql&amp;gt;&amp;nbsp;exit&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 20px;&quot;&gt;5. 用新密码登陆&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;pre class=&quot;brush:bash;toolbar:false&quot;&gt;mysql&amp;nbsp;-u&amp;nbsp;root&amp;nbsp;-p&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;','2017-02-24',1,9,1),(3,'LNMP环境的搭建之php篇','&lt;p&gt;&lt;span style=&quot;font-size: 20px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;strong&gt;1. 安装php和需要使用的扩展&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;pre class=&quot;brush:bash;toolbar:false&quot;&gt;sudo&amp;nbsp;yum&amp;nbsp;install&amp;nbsp;php&amp;nbsp;php-mysql&amp;nbsp;php-fpm&amp;nbsp;php-gd&amp;nbsp;php-pdo&amp;nbsp;php-mbstring&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-size: 20px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 16px;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 20px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;2. 配置nginx让其支持对php脚本的解析以及pathinfo的支持&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 20px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 16px;&quot;&gt;配置文件的路径为&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:bash;toolbar:false&quot;&gt;sudo&amp;nbsp;vi&amp;nbsp;/etc/nginx/conf.d/default.conf&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:微软雅黑, Microsoft YaHei&quot;&gt;将location块中的root放入server块中，并配置编码&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:bash;toolbar:false&quot;&gt;server&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;listen&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;80;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;server_name&amp;nbsp;&amp;nbsp;localhost;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;root&amp;nbsp;&amp;nbsp;&amp;nbsp;/usr/share/nginx/html;\r\n\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;charset&amp;nbsp;utf-8;\r\n\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;location&amp;nbsp;/&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;index&amp;nbsp;&amp;nbsp;index.html&amp;nbsp;index.htm&amp;nbsp;index.php;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;error_page&amp;nbsp;&amp;nbsp;&amp;nbsp;500&amp;nbsp;502&amp;nbsp;503&amp;nbsp;504&amp;nbsp;&amp;nbsp;/50x.html;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;location&amp;nbsp;=&amp;nbsp;/50x.html&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;root&amp;nbsp;&amp;nbsp;&amp;nbsp;/usr/share/nginx/html;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family:微软雅黑, Microsoft YaHei&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 20px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 16px;&quot;&gt;配置对php的解析&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:bash;toolbar:false&quot;&gt;location&amp;nbsp;~&amp;nbsp;\\.php&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;fastcgi_pass&amp;nbsp;&amp;nbsp;&amp;nbsp;127.0.0.1:9000;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;fastcgi_index&amp;nbsp;&amp;nbsp;index.php;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;fastcgi_param&amp;nbsp;&amp;nbsp;SCRIPT_FILENAME&amp;nbsp;&amp;nbsp;/scripts$fastcgi_script_name;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;include&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;fastcgi_params;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;set&amp;nbsp;$path_info&amp;nbsp;&amp;quot;&amp;quot;;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;set&amp;nbsp;$real_script_name&amp;nbsp;$fastcgi_script_name;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;($fastcgi_script_name&amp;nbsp;~&amp;nbsp;&amp;quot;^(.+?\\.php)(/.+)$&amp;quot;)&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;set&amp;nbsp;$real_script_name&amp;nbsp;$1;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;set&amp;nbsp;$path_info&amp;nbsp;$2;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;fastcgi_param&amp;nbsp;SCRIPT_FILENAME&amp;nbsp;$document_root$real_script_name;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;fastcgi_param&amp;nbsp;SCRIPT_NAME&amp;nbsp;$real_script_name;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;fastcgi_param&amp;nbsp;PATH_INFO&amp;nbsp;$path_info;\r\n}&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-size: 20px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 16px;&quot;&gt;&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 20px;&quot;&gt;&lt;strong&gt;3. 设置开机自启动&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:bash;toolbar:false&quot;&gt;sudo&amp;nbsp;chkconfig&amp;nbsp;php-fpm&amp;nbsp;on&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-size: 20px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 16px;&quot;&gt;&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 20px;&quot;&gt;&lt;strong&gt;4. 启动php-fpm&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:bash;toolbar:false&quot;&gt;sudo&amp;nbsp;service&amp;nbsp;php-fpm&amp;nbsp;start&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 20px;&quot;&gt;&lt;/span&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 16px;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 20px;&quot;&gt;&lt;strong&gt;5. 测试是否成功&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;网站默认地址为：&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;pre class=&quot;brush:bash;toolbar:false&quot;&gt;/usr/share/nginx/html&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;font face=&quot;微软雅黑, Microsoft YaHei&quot;&gt;修改目录权限为你当前登录的用户，这样新建/修改其中的目录/文件无需sudo了&lt;/font&gt;&lt;/p&gt;&lt;pre class=&quot;brush:bash;toolbar:false&quot;&gt;sudo&amp;nbsp;chown&amp;nbsp;-R&amp;nbsp;yb.users&amp;nbsp;/usr/share/nginx/html&lt;/pre&gt;&lt;p&gt;&lt;font face=&quot;微软雅黑, Microsoft YaHei&quot;&gt;&lt;/font&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;font face=&quot;微软雅黑, Microsoft YaHei&quot;&gt;现在可以测试了&lt;/font&gt;&lt;/p&gt;&lt;pre class=&quot;brush:bash;toolbar:false&quot;&gt;cd&amp;nbsp;/usr/share/nginx/html\r\nvi&amp;nbsp;index.php&lt;/pre&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;lt;?php\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;phpinfo();&lt;/pre&gt;&lt;p&gt;&lt;font face=&quot;微软雅黑, Microsoft YaHei&quot;&gt;&lt;br/&gt;&lt;/font&gt;&lt;/p&gt;&lt;p&gt;&lt;font face=&quot;微软雅黑, Microsoft YaHei&quot;&gt;保存后打开浏览器输入IP地址即可&lt;/font&gt;&lt;/p&gt;','2017-02-24',1,15,1),(4,'引入Thinkphp框架','&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 20px;&quot;&gt;1. 下载框架&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:微软雅黑, Microsoft YaHei&quot;&gt;进入&lt;a href=&quot;http://www.thinkphp.cn/down.html&quot; target=&quot;_blank&quot; title=&quot;THINKPHP&quot;&gt;THINKPHP&lt;/a&gt;官网下载页，如下：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:微软雅黑, Microsoft YaHei&quot;&gt;&lt;img src=&quot;/upload/image/20170224/1487913432475893.png&quot; title=&quot;1487913432475893.png&quot; alt=&quot;12.PNG&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;按需求下载，本博客使用的是&lt;a href=&quot;http://www.thinkphp.cn/down/610.html&quot; target=&quot;_blank&quot; title=&quot;ThinkPHP3.2.3完整版&quot;&gt;THINKPHP3.2.3完整版&lt;/a&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 20px;&quot;&gt;&lt;strong&gt;2. 引入框架&lt;/strong&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;font face=&quot;微软雅黑, Microsoft YaHei&quot;&gt;将已经下载好的压缩文件解压，并放入网站根目录即可，目录结构类似以下：&lt;/font&gt;&lt;/p&gt;&lt;pre class=&quot;brush:bash;toolbar:false;&quot;&gt;www&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;WEB部署目录（或者子目录）\r\n├─index.php&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;入口文件\r\n├─README.md&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;README文件\r\n├─Application&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;应用目录\r\n├─Public&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;资源文件目录\r\n└─ThinkPHP&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;框架目录&lt;/pre&gt;&lt;p&gt;&lt;font face=&quot;微软雅黑, Microsoft YaHei&quot;&gt;&lt;/font&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 20px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;3. 测试是否引入成功&lt;/span&gt;&lt;/strong&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;进入浏览器并输入IP地址，若出现以下情况则说明引入成功。&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;img src=&quot;/upload/image/20170224/1487914192326036.jpg&quot; title=&quot;1487914192326036.jpg&quot; alt=&quot;hello.jpg&quot;/&gt;&lt;/span&gt;&lt;/p&gt;','2017-02-24',1,16,1),(5,'控制器中登录方法的实现','&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 20px;&quot;&gt;1. 判断是否有POST提交数据&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:微软雅黑, Microsoft YaHei&quot;&gt;如果检测到有POST上来的数据，表明用户正在进行提交登录表单的操作，此时需要进行相关的检测工作；相反，说明用户刚刚进入登录页面，此时需要展示登录页面前端模板。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:微软雅黑, Microsoft YaHei&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;font-family:微软雅黑, Microsoft YaHei&quot;&gt;有POST数据：&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;//&amp;nbsp;对外访问方法&amp;quot;login&amp;quot;\r\npublic&amp;nbsp;function&amp;nbsp;login(){\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if(IS_POST)&amp;nbsp;{&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;span style=&quot;font-family:微软雅黑, Microsoft YaHei&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;本博客系统只设一个管理员，故将管理员信息保存在PHP文件中（非数据库内）&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;进行登录操作\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;用户名、密码保存在文件中\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$user&amp;nbsp;=&amp;nbsp;&amp;#39;YourUserName&amp;#39;;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$pasd&amp;nbsp;=&amp;nbsp;&amp;#39;YourPassword&amp;#39;;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;检测验证码\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$verify&amp;nbsp;=&amp;nbsp;new&amp;nbsp;\\Think\\Verify();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if(!$verify-&amp;gt;check(I(&amp;#39;post.code&amp;#39;))){\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;error(&amp;#39;验证码错误&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;注意：需要手动结束脚本运行\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;die;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;验证\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if(I(&amp;#39;post.username&amp;#39;)===$user&amp;nbsp;&amp;amp;&amp;amp;&amp;nbsp;I(&amp;#39;post.password&amp;#39;)===$pasd)&amp;nbsp;{&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;只有一个管理员，登录状态保持可以这样来&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;保存登录状态，\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;session(&amp;#39;is_login&amp;#39;,&amp;nbsp;&amp;#39;1&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;success(&amp;#39;登录成功&amp;#39;,U(&amp;#39;Manage/artlist&amp;#39;));\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;error(&amp;#39;登录失败&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;无POST数据：&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;此时需要考虑用户是否重复登陆&lt;/span&gt;&lt;strong&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;首次进入管理页面\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;判断是否有已登录的&amp;quot;SESSION&amp;quot;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if(session(&amp;#39;is_login&amp;#39;))&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;直接跳转到后台管理页面\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;error(&amp;#39;无需重复登录&amp;#39;,U(&amp;#39;Manage/artlist&amp;#39;));&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;没有重复登录的话，直接展示LOGIN模板&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;展示模板\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this-&amp;gt;display();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n}&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;最后附上完整代码（图）：&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;img src=&quot;/upload/image/20170224/1487919043152834.png&quot; title=&quot;1487919043152834.png&quot; alt=&quot;13.PNG&quot;/&gt;&lt;/span&gt;&lt;/p&gt;','2017-02-24',4,19,1),(6,'配置修改之增加模板常量','&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;为减少代码的书写量，以及方便后期文件夹的转移，现将本博客系统的外部引入文件统一放入指定文件夹，并设置模板常量。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;修改分组配置文件&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:bash;toolbar:false&quot;&gt;vi&amp;nbsp;Application/Blog/Conf/config.php&lt;/pre&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;lt;?php\r\nreturn&amp;nbsp;array(\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;#39;配置项&amp;#39;=&amp;gt;&amp;#39;配置值&amp;#39;\r\n\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;自定义模板常量\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;TMPL_PARSE_STRING&amp;#39;&amp;nbsp;=&amp;gt;&amp;nbsp;array(\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;#39;__BLOG__&amp;#39;&amp;nbsp;=&amp;gt;&amp;nbsp;&amp;#39;/Public/Blog&amp;#39;,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;)\r\n);&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;/span&gt;&lt;/p&gt;','2017-02-24',5,12,1),(7,'视图模板登录页面中插入验证码标签','&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;本博客模板的登录页面不自带验证码标签，为安全起见，需自行加入&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;pre class=&quot;brush:html;toolbar:false&quot;&gt;&amp;lt;form&amp;nbsp;action=&amp;quot;#&amp;quot;&amp;nbsp;method=&amp;quot;post&amp;quot;&amp;gt;\r\n&amp;lt;p&amp;gt;\r\n&amp;lt;input&amp;nbsp;placeholder=&amp;quot;用户名&amp;quot;&amp;nbsp;name=&amp;quot;username&amp;quot;&amp;nbsp;type=&amp;quot;text&amp;quot;&amp;nbsp;value=&amp;quot;&amp;quot;&amp;nbsp;size=&amp;quot;30&amp;quot;&amp;gt;\r\n&amp;lt;/p&amp;gt;&amp;lt;p&amp;gt;\r\n&amp;lt;input&amp;nbsp;placeholder=&amp;quot;密&amp;amp;nbsp;&amp;amp;nbsp;&amp;amp;nbsp;码&amp;quot;&amp;nbsp;name=&amp;quot;password&amp;quot;&amp;nbsp;type=&amp;quot;text&amp;quot;&amp;nbsp;value=&amp;quot;&amp;quot;&amp;nbsp;size=&amp;quot;30&amp;quot;&amp;gt;\r\n&amp;lt;/p&amp;gt;&amp;lt;p&amp;gt;\r\n&amp;lt;img&amp;nbsp;src=&amp;#39;__CONTROLLER__/code&amp;#39;&amp;nbsp;onclick=&amp;quot;this.src=&amp;#39;__CONTROLLER__/code&amp;#39;&amp;quot;&amp;nbsp;style=&amp;quot;margin-bottom:10px;&amp;quot;&amp;nbsp;/&amp;gt;\r\n&amp;lt;/p&amp;gt;&amp;lt;p&amp;gt;\r\n&amp;lt;input&amp;nbsp;placeholder=&amp;quot;验证码&amp;quot;&amp;nbsp;name=&amp;quot;code&amp;quot;&amp;nbsp;type=&amp;quot;text&amp;quot;&amp;nbsp;value=&amp;quot;&amp;quot;&amp;nbsp;size=&amp;quot;30&amp;quot;&amp;gt;\r\n&amp;lt;/p&amp;gt;&amp;lt;p&amp;gt;\r\n&amp;lt;input&amp;nbsp;type=&amp;quot;submit&amp;quot;&amp;nbsp;value=&amp;quot;登陆&amp;quot;&amp;gt;\r\n&amp;lt;/p&amp;gt;\r\n&amp;lt;/form&amp;gt;&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;','2017-02-24',3,12,1),(8,'新建数据库及相应表','&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 20px;&quot;&gt;1. 常用操作&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 16px;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;1.创建用户&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;CREATE&amp;nbsp;USER&amp;nbsp;&amp;#39;username&amp;#39;@&amp;#39;host&amp;#39;&amp;nbsp;IDENTIFIED&amp;nbsp;BY&amp;nbsp;&amp;#39;password&amp;#39;;&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 16px;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;2.创建utf-8字符集数据库&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;CREATE&amp;nbsp;DATABASE&amp;nbsp;db_name&amp;nbsp;DEFAULT&amp;nbsp;CHARACTER&amp;nbsp;SET&amp;nbsp;utf8&amp;nbsp;COLLATE&amp;nbsp;utf8_general_ci;&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;3.更改数据库的字符编码&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;若数据库已存在，并需要修改其编码的话，就执行以下操作&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;ALTER&amp;nbsp;DATABASE&amp;nbsp;db_name&amp;nbsp;DEFAULT&amp;nbsp;CHARACTER&amp;nbsp;SET&amp;nbsp;utf8&amp;nbsp;COLLATE&amp;nbsp;utf8_general_ci&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 16px;&quot;&gt;&lt;strong&gt;4.授权&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;GRANT&amp;nbsp;privileges&amp;nbsp;ON&amp;nbsp;databasename.tablename&amp;nbsp;TO&amp;nbsp;&amp;#39;username&amp;#39;@&amp;#39;host&amp;#39;&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;其中privileges有select，insert，update，delete，create，drop，index，alter，grant，references，reload，shutdown，process，file这14个权限，可以分配多个权限，以&amp;quot;,&amp;quot;隔开，或直接用all，代表全部权限。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;databasename-数据库名,tablename-表名,如果要授予该用户对所有数据库和表的相应操作权限则可用*表示, 如*.*&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 16px;&quot;&gt;&lt;strong&gt;5.设置与更改用户密码&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;SET&amp;nbsp;PASSWORD&amp;nbsp;FOR&amp;nbsp;&amp;#39;username&amp;#39;@&amp;#39;host&amp;#39;&amp;nbsp;=&amp;nbsp;PASSWORD(&amp;#39;newpassword&amp;#39;);&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 16px;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;6.撤销用户权限&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;REVOKE&amp;nbsp;privilege&amp;nbsp;ON&amp;nbsp;databasename.tablename&amp;nbsp;FROM&amp;nbsp;&amp;#39;username&amp;#39;@&amp;#39;host&amp;#39;;&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 16px;&quot;&gt;&lt;strong&gt;7.删除用户&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;DROP&amp;nbsp;USER&amp;nbsp;&amp;#39;username&amp;#39;@&amp;#39;host&amp;#39;;&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 16px;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;8.新建表&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;CREATE&amp;nbsp;TABLE&amp;nbsp;table_name&amp;nbsp;(column_name&amp;nbsp;column_type);&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size: 20px;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-size: 20px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;2. 数据表的设计&lt;/span&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;文章内容存放&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;CREATE&amp;nbsp;TABLE&amp;nbsp;blog_art(\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;id&amp;nbsp;INT&amp;nbsp;NOT&amp;nbsp;NULL&amp;nbsp;AUTO_INCREMENT,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;title&amp;nbsp;VARCHAR(100)&amp;nbsp;NOT&amp;nbsp;NULL,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;content&amp;nbsp;TEXT&amp;nbsp;NOT&amp;nbsp;NULL,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;cre_date&amp;nbsp;DATE&amp;nbsp;NOT&amp;nbsp;NULL,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;cat_id&amp;nbsp;TINYINT&amp;nbsp;NOT&amp;nbsp;NULL,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ans_number&amp;nbsp;SMALLINT&amp;nbsp;NOT&amp;nbsp;NULL&amp;nbsp;DEFAULT&amp;nbsp;0,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;status&amp;nbsp;TINYINT&amp;nbsp;NOT&amp;nbsp;NULL&amp;nbsp;DEFAULT&amp;nbsp;1,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;PRIMARY&amp;nbsp;KEY&amp;nbsp;(&amp;nbsp;id&amp;nbsp;)\r\n)&amp;nbsp;ENGINE=MyISAM&amp;nbsp;DEFAULT&amp;nbsp;CHARSET=utf8;&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; font-size: 16px;&quot;&gt;栏目存放&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;CREATE&amp;nbsp;TABLE&amp;nbsp;blog_cat(\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;id&amp;nbsp;INT&amp;nbsp;NOT&amp;nbsp;NULL&amp;nbsp;AUTO_INCREMENT,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;name&amp;nbsp;VARCHAR(30)&amp;nbsp;NOT&amp;nbsp;NULL,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;art_number&amp;nbsp;INT&amp;nbsp;NOT&amp;nbsp;NULL&amp;nbsp;DEFAULT&amp;nbsp;0,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;PRIMARY&amp;nbsp;KEY&amp;nbsp;(&amp;nbsp;id&amp;nbsp;)\r\n)&amp;nbsp;ENGINE=MyISAM&amp;nbsp;DEFAULT&amp;nbsp;CHARSET=utf8;&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;用户评论&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;CREATE&amp;nbsp;TABLE&amp;nbsp;blog_ans(\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;id&amp;nbsp;INT&amp;nbsp;NOT&amp;nbsp;NULL&amp;nbsp;AUTO_INCREMENT,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;name&amp;nbsp;VARCHAR(30)&amp;nbsp;NOT&amp;nbsp;NULL,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;email&amp;nbsp;VARCHAR(100)&amp;nbsp;NOT&amp;nbsp;NULL&amp;nbsp;DEFAULT&amp;nbsp;&amp;#39;&amp;#39;,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;art_id&amp;nbsp;INT&amp;nbsp;NOT&amp;nbsp;NULL,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;content&amp;nbsp;VARCHAR(800)&amp;nbsp;NOT&amp;nbsp;NULL,\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;cre_time&amp;nbsp;timestamp&amp;nbsp;NOT&amp;nbsp;NULL&amp;nbsp;DEFAULT&amp;nbsp;now(),\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;PRIMARY&amp;nbsp;KEY&amp;nbsp;(&amp;nbsp;id&amp;nbsp;)\r\n)&amp;nbsp;ENGINE=MyISAM&amp;nbsp;DEFAULT&amp;nbsp;CHARSET=utf8;&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;','2017-02-24',1,12,1),(9,'控制器中文章列表的方法实现','&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;根据数据表的设计，以及前台需要展示的内容，此处要用到&lt;strong&gt;联表查询&lt;/strong&gt;的操作，故实例化空类。&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;//&amp;nbsp;对外被访问方法&amp;quot;artlist&amp;quot;\r\npublic&amp;nbsp;function&amp;nbsp;artlist(){\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;实例化数据库类模型\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$model&amp;nbsp;=&amp;nbsp;M();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$count&amp;nbsp;=&amp;nbsp;$model&amp;nbsp;-&amp;gt;&amp;nbsp;table(&amp;#39;blog_art&amp;#39;)&amp;nbsp;-&amp;gt;&amp;nbsp;count();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;实例化分页类模型\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$page&amp;nbsp;=&amp;nbsp;new&amp;nbsp;Page($count,&amp;nbsp;15);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;分页显示输出\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$show&amp;nbsp;=&amp;nbsp;$page&amp;nbsp;-&amp;gt;&amp;nbsp;show();&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;多表查询数据，原始SQL语句：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;&amp;quot;SELECT&amp;nbsp;A.id,A.cre_date,A.title,B.name,A.ans_number,A.status\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;FROM&amp;nbsp;blog_art&amp;nbsp;as&amp;nbsp;A,&amp;nbsp;blog_cat&amp;nbsp;as&amp;nbsp;B\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;WHERE&amp;nbsp;A.cat_id&amp;nbsp;=&amp;nbsp;B.id\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;LIMIT&amp;nbsp;$page-&amp;gt;firstRow,&amp;nbsp;$page-&amp;gt;listRows\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;ORDER&amp;nbsp;BY&amp;nbsp;A.id&amp;nbsp;desc;&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;连续操作后：&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;联表查询数据\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$data&amp;nbsp;=&amp;nbsp;$model&amp;nbsp;-&amp;gt;&amp;nbsp;field(&amp;#39;A.id,A.cre_date,A.title,B.name,A.ans_number,A.status&amp;#39;)\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;table(&amp;#39;blog_art&amp;nbsp;as&amp;nbsp;A,&amp;nbsp;blog_cat&amp;nbsp;as&amp;nbsp;B&amp;#39;)\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;where(&amp;#39;A.cat_id&amp;nbsp;=&amp;nbsp;B.id&amp;#39;)\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;limit($page-&amp;gt;firstRow.&amp;#39;,&amp;#39;.$page-&amp;gt;listRows)\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;order(&amp;#39;A.id&amp;nbsp;desc&amp;#39;)\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;-&amp;gt;&amp;nbsp;select();&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;为前台美观，需要限制标题长度，标题中英文混输，此时要用到 “mb_string”扩展中的函数“mb_substr”。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;前台有数字排序，可以考虑&amp;lt;ol&amp;gt;标签，或者于后台循环加入排序数字。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:微软雅黑, Microsoft YaHei&quot;&gt;for语句可用foreach($data as $value)替换。&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;添加排序,并截取标题\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$length&amp;nbsp;=&amp;nbsp;count($data);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;for($i=0;&amp;nbsp;$i&amp;lt;$length;&amp;nbsp;$i++){\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;添加排序\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$data[$i][&amp;#39;t_id&amp;#39;]&amp;nbsp;=&amp;nbsp;$i+1;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;截取标题长度\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$temp&amp;nbsp;=&amp;nbsp;mb_substr($data[$i][&amp;#39;title&amp;#39;],&amp;nbsp;0,&amp;nbsp;20,&amp;nbsp;&amp;#39;utf-8&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;选择地添加&amp;#39;...&amp;#39;字符串\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if($temp&amp;nbsp;!=&amp;nbsp;$data[$i][&amp;#39;title&amp;#39;])\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$temp&amp;nbsp;.=&amp;nbsp;&amp;#39;...&amp;#39;;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;整理数组\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$data[$i][&amp;#39;title&amp;#39;]&amp;nbsp;=&amp;nbsp;$temp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;接下来就是分配变量以及输出模板&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;分配二维数组\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this&amp;nbsp;-&amp;gt;&amp;nbsp;assign(&amp;#39;data&amp;#39;,&amp;nbsp;$data);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this&amp;nbsp;-&amp;gt;&amp;nbsp;assign(&amp;#39;pages&amp;#39;,&amp;nbsp;$show);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;展示模板\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this&amp;nbsp;-&amp;gt;&amp;nbsp;display();\r\n}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;最后附上完整代码（图）：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;img src=&quot;/upload/image/20170224/1487925354392336.png&quot; title=&quot;1487925354392336.png&quot; alt=&quot;14.PNG&quot;/&gt;&lt;/span&gt;&lt;/p&gt;','2017-02-24',4,18,1),(10,'文章列表中引入外部扩展','&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;于发布或隐藏文章时使用，效果如下：&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;img src=&quot;/upload/image/20170224/1487926254147385.png&quot; title=&quot;1487926254147385.png&quot; alt=&quot;15.PNG&quot;/&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;img src=&quot;/upload/image/20170224/1487926449865093.png&quot; title=&quot;1487926449865093.png&quot; alt=&quot;16.PNG&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;h1&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;1. 下载LAYER&lt;/span&gt;&lt;br/&gt;&lt;/h1&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;a href=&quot;http://layer.layui.com/&quot; target=&quot;_blank&quot; title=&quot;官网&quot;&gt;官网&lt;/a&gt;，将解压出来的文件放入/Public/Blog/plugin/layer下，然后在你的前端代码中插入它里面的JS文件和CSS文件，又因为LAYER是用JQUERY代码写的，所以还要引入JQUERY文件。&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:html;toolbar:false&quot;&gt;{/*&amp;nbsp;如果不引入css文件，你的弹窗会先出现短暂的document.write现象&amp;nbsp;*/}\r\n&amp;lt;link&amp;nbsp;rel=&amp;quot;stylesheet&amp;quot;&amp;nbsp;href=&amp;quot;__BLOG__/plugin/layer/skin/default/layer.css&amp;quot;&amp;gt;\r\n{/*&amp;nbsp;jquery文件要先引入进来&amp;nbsp;*/}\r\n&amp;lt;script&amp;nbsp;src=&amp;#39;__BLOG__/js/jquery.js&amp;#39;&amp;gt;&amp;lt;/script&amp;gt;\r\n&amp;lt;script&amp;nbsp;src=&amp;quot;__BLOG__/plugin/layer/layer.js&amp;quot;&amp;gt;&amp;lt;/script&amp;gt;&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;前端点击事件实现如下：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:html;toolbar:false&quot;&gt;&amp;lt;a&amp;nbsp;href=&amp;quot;javascript:void(0);&amp;quot;&amp;nbsp;onclick=&amp;quot;layer.confirm(\r\n&amp;nbsp;&amp;#39;确认&amp;lt;if&amp;nbsp;condition=&amp;quot;$vo.status&amp;nbsp;eq&amp;nbsp;1&amp;quot;&amp;gt;隐藏&amp;lt;else&amp;nbsp;/&amp;gt;显示&amp;lt;/if&amp;gt;操作？&amp;#39;,\r\n&amp;nbsp;{btn:&amp;nbsp;[&amp;#39;确认&amp;#39;,&amp;#39;取消&amp;#39;]},\r\n&amp;nbsp;function(){top.location.href=&amp;#39;__CONTROLLER__/hide/id/{$vo.id}/status/{$vo.status}&amp;#39;});&amp;quot;&amp;gt;\r\n&amp;lt;switch&amp;nbsp;name=&amp;#39;vo.status&amp;#39;&amp;gt;\r\n&amp;nbsp;&amp;lt;case&amp;nbsp;value=&amp;quot;1&amp;quot;&amp;gt;已发布&amp;lt;/case&amp;gt;\r\n&amp;nbsp;&amp;lt;case&amp;nbsp;value=&amp;quot;0&amp;quot;&amp;gt;已隐藏&amp;lt;/case&amp;gt;\r\n&amp;lt;/switch&amp;gt;&amp;lt;/a&amp;gt;&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;','2017-02-24',3,8,1),(11,'控制器中发布文章方法的实现','&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;类似登录页面，首先要做判断，是否有POST数据上来&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;//&amp;nbsp;对外被访问方法&amp;quot;artadd&amp;quot;\r\npublic&amp;nbsp;function&amp;nbsp;artadd(){\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;判断有无POST数据\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if(IS_POST)&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;提交数据\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$post&amp;nbsp;=&amp;nbsp;I(&amp;#39;post.&amp;#39;);&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;此时，因为建表的时候没有使用默认时间函数，所以接下来要指定上传的时间&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;若数据库设置默认时间，则无需生成\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$post[&amp;#39;cre_date&amp;#39;]&amp;nbsp;=&amp;nbsp;date(&amp;#39;Y-m-d&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;方便操作\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$id&amp;nbsp;=&amp;nbsp;I(&amp;#39;post.cat_id&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;实例化模型\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$model&amp;nbsp;=&amp;nbsp;M(&amp;#39;art&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;向数据库中添加数据\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$result&amp;nbsp;=&amp;nbsp;$model&amp;nbsp;-&amp;gt;&amp;nbsp;data($post)&amp;nbsp;-&amp;gt;&amp;nbsp;add();&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;font face=&quot;微软雅黑, Microsoft YaHei&quot;&gt;若添加成功，还需要更新blog_cat表中相应的字段，以求减少MYSQL压力。&lt;/font&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if($result)&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;更新&amp;quot;cat&amp;quot;表中的&amp;quot;art_number&amp;quot;字段\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$resu&amp;nbsp;=&amp;nbsp;M()&amp;nbsp;-&amp;gt;&amp;nbsp;table(&amp;#39;blog_cat&amp;#39;)&amp;nbsp;-&amp;gt;&amp;nbsp;where(&amp;quot;id&amp;nbsp;=&amp;nbsp;$id&amp;quot;)&amp;nbsp;-&amp;gt;&amp;nbsp;setInc(&amp;#39;art_number&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this&amp;nbsp;-&amp;gt;&amp;nbsp;success(&amp;#39;添加成功&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this&amp;nbsp;-&amp;gt;&amp;nbsp;error(&amp;#39;添加失败&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&lt;/pre&gt;&lt;p&gt;&lt;font face=&quot;微软雅黑, Microsoft YaHei&quot;&gt;&lt;/font&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;font face=&quot;微软雅黑, Microsoft YaHei&quot;&gt;接下来就是未提交数据时默认显示的页面&lt;/font&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;默认显示\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;实例化模型\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$model&amp;nbsp;=&amp;nbsp;M(&amp;#39;cat&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;获得数据\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$result&amp;nbsp;=&amp;nbsp;$model&amp;nbsp;-&amp;gt;&amp;nbsp;field(&amp;#39;id,name&amp;#39;)&amp;nbsp;-&amp;gt;&amp;nbsp;select();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;分配数组\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this&amp;nbsp;-&amp;gt;&amp;nbsp;assign(&amp;#39;data&amp;#39;,&amp;nbsp;$result);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;展示模板\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this&amp;nbsp;-&amp;gt;&amp;nbsp;display();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n}&lt;/pre&gt;&lt;p&gt;&lt;font face=&quot;微软雅黑, Microsoft YaHei&quot;&gt;&lt;/font&gt;&lt;br/&gt;&lt;/p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;最后附上完整代码（图）：&lt;/span&gt;&lt;p&gt;&lt;font face=&quot;微软雅黑, Microsoft YaHei&quot;&gt;&lt;img src=&quot;/upload/image/20170224/1487931016420547.png&quot; title=&quot;1487931016420547.png&quot; alt=&quot;18.PNG&quot;/&gt;&lt;/font&gt;&lt;/p&gt;','2017-02-24',4,14,1),(12,'发布文章前端引入UE扩展','<p><span style=\"font-family:微软雅黑, Microsoft YaHei\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">引入后效果如下：</span></span></p><p><span style=\"font-family:微软雅黑, Microsoft YaHei\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><img src=\"/upload/image/20170224/1487931330116725.png\" title=\"1487931330116725.png\" alt=\"19.PNG\"/></span></span></p><p><span style=\"font-family:微软雅黑, Microsoft YaHei\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><br/></span></span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><a href=\"http://ueditor.baidu.com/website/\" target=\"_blank\" title=\"官网\">官网</a>，下载解压后，将其中的utf8-php文件夹放入/Public/Blog/plugin下，并改名为ue。打开ue下index.html文件，找到里面的相关代码，写入发布文章前端代码中具体如下：</span><br/></p><pre class=\"brush:html;toolbar:false\">{/*&nbsp;UEDITOR要求引入文件&nbsp;*/} &lt;script&nbsp;src=&#39;__BLOG__/plugin/ue/ueditor.config.js&#39;&gt;&lt;/script&gt; &lt;script&nbsp;src=&#39;__BLOG__/plugin/ue/ueditor.all.min.js&#39;&gt;&lt;/script&gt; &lt;script&nbsp;src=&#39;__BLOG__/plugin/ue/lang/zh-cn/zh-cn.js&#39;&gt;&lt;/script&gt; ...... &lt;script&gt; var&nbsp;ue&nbsp;=&nbsp;UE.getEditor(&#39;editor&#39;); &lt;/script&gt;</pre><p><br/></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; color: rgb(192, 0, 0);\">注意：容器是&lt;script&gt;&lt;/script&gt;标签，而不是html标签</span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; color: rgb(192, 0, 0);\">注意：form表单中上传内容的名字也由name指定</span></p><pre class=\"brush:html;toolbar:false\">&lt;script&nbsp;id=&quot;editor&quot;&nbsp;name=&quot;content&quot;&nbsp;type=&quot;text/plain&quot;&nbsp;style=&quot;width:518px;height:300px;&quot;&gt;&lt;/script&gt;</pre><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; color: rgb(0, 0, 0);\"><br/></span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">若有图片，则最好指定一下图片的路径</span><br/></p><pre class=\"brush:bash;toolbar:false\">vi&nbsp;/Public/Blog/plugin/ue/php/config.jso</pre><pre class=\"brush:cpp;toolbar:false\">&quot;scrawlPathFormat&quot;:&nbsp;&quot;/upload/image/{yyyy}{mm}{dd}/{time}{rand:6}&quot;,</pre><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; color: rgb(192, 0, 0);\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; color: rgb(0, 0, 0);\"><br/></span></span></p><p><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; color: rgb(192, 0, 0);\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; color: rgb(0, 0, 0);\">新建上传图片保存路径（当前位置是网站根目录），并更改属主</span></span></p><pre class=\"brush:bash;toolbar:false\">mkdir&nbsp;-pv&nbsp;upload/image sudo&nbsp;chown&nbsp;apache.apache&nbsp;upload/image</pre><p></p>','2017-02-24',3,20,0),(13,'发布文章中引入外部扩展','&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;引入后效果如下：&lt;img src=&quot;/upload/image/20170224/1487935069264522.png&quot; title=&quot;1487935069264522.png&quot; alt=&quot;19.PNG&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;a href=&quot;http://ueditor.baidu.com/website/&quot; target=&quot;_blank&quot; title=&quot;官网&quot; style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; text-decoration: underline;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;官网&lt;/span&gt;&lt;/a&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;，下载解压后，将其中的utf8-php文件夹放入/Public/Blog/plugin下，并改名为ue。打开ue下index.html文件，找到里面的相关代码，写入发布文章前端代码中具体如下：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:html;toolbar:false&quot;&gt;{/*&amp;nbsp;UEDITOR要求引入文件&amp;nbsp;*/}\r\n&amp;lt;script&amp;nbsp;src=&amp;#39;__BLOG__/plugin/ue/ueditor.config.js&amp;#39;&amp;gt;&amp;lt;/script&amp;gt;\r\n&amp;lt;script&amp;nbsp;src=&amp;#39;__BLOG__/plugin/ue/ueditor.all.min.js&amp;#39;&amp;gt;&amp;lt;/script&amp;gt;\r\n&amp;lt;script&amp;nbsp;src=&amp;#39;__BLOG__/plugin/ue/lang/zh-cn/zh-cn.js&amp;#39;&amp;gt;&amp;lt;/script&amp;gt;\r\n...\r\n&amp;lt;script&amp;gt;var&amp;nbsp;ue&amp;nbsp;=&amp;nbsp;UE.getEditor(&amp;#39;editor&amp;#39;);&amp;lt;/script&amp;gt;&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color: rgb(192, 0, 0); font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;注意：容器是标签，而不是html标签&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color: rgb(192, 0, 0); font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;注意：form表单中上传内容的名字也由name指定&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:html;toolbar:false&quot;&gt;&amp;lt;script&amp;nbsp;id=&amp;quot;editor&amp;quot;&amp;nbsp;name=&amp;quot;content&amp;quot;&amp;nbsp;type=&amp;quot;text/plain&amp;quot;&amp;nbsp;style=&amp;quot;width:518px;height:300px;&amp;quot;&amp;gt;&amp;lt;/script&amp;gt;&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;若有图片，则最好指定一下图片的路径&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:bash;toolbar:false&quot;&gt;vi&amp;nbsp;/Public/Blog/plugin/ue/php/config.json&lt;/pre&gt;&lt;pre class=&quot;brush:html;toolbar:false&quot;&gt;&amp;quot;imagePathFormat&amp;quot;:&amp;nbsp;&amp;quot;/upload/image/{yyyy}{mm}{dd}/{time}{rand:6}&amp;quot;,&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;新建上传图片保存路径（当前位置是网站根目录），并更改属主&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:bash;toolbar:false&quot;&gt;mkdir&amp;nbsp;-pv&amp;nbsp;upload/image\r\nchown&amp;nbsp;apache.apache&amp;nbsp;upload/image&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;如要代码高亮，则需引用以下资源文件（均位于ueditor目录中）：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:html;toolbar:false&quot;&gt;{//&amp;nbsp;ue&amp;nbsp;代码高亮}\r\n&amp;lt;link&amp;nbsp;rel=&amp;quot;stylesheet&amp;quot;&amp;nbsp;href=&amp;quot;__BLOG__/plugin/ue/third-party/SyntaxHighlighter/shCoreDefault.css&amp;quot;&amp;gt;\r\n&amp;lt;script&amp;nbsp;src=&amp;quot;__BLOG__/plugin/ue/third-party/SyntaxHighlighter/shCore.js&amp;quot;&amp;gt;&amp;lt;/script&amp;gt;\r\n&amp;lt;script&amp;gt;SyntaxHighlighter.all();&amp;lt;/script&amp;gt;&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;','2017-02-24',3,17,1),(14,'控制器中栏目列表方法的实现','&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;//&amp;nbsp;对外被访问方法&amp;quot;catlist&amp;quot;\r\npublic&amp;nbsp;function&amp;nbsp;catlist(){\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;实例化模型\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$model&amp;nbsp;=&amp;nbsp;M(&amp;#39;cat&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;查询数据\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$data&amp;nbsp;&amp;nbsp;=&amp;nbsp;$model&amp;nbsp;-&amp;gt;&amp;nbsp;field(&amp;#39;name,art_number&amp;#39;)&amp;nbsp;-&amp;gt;&amp;nbsp;limit(0,10)&amp;nbsp;-&amp;gt;&amp;nbsp;order(&amp;#39;id&amp;nbsp;desc&amp;#39;)&amp;nbsp;-&amp;gt;&amp;nbsp;select();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;添加序号\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$length&amp;nbsp;=&amp;nbsp;count($data);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;for($i=0;&amp;nbsp;$i&amp;lt;$length;&amp;nbsp;$i++)&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;添加序号\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$data[$i][&amp;#39;id&amp;#39;]&amp;nbsp;=&amp;nbsp;$i+1;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;分配二维数组\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this&amp;nbsp;-&amp;gt;&amp;nbsp;assign(&amp;#39;data&amp;#39;,&amp;nbsp;$data);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;展示模板\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this&amp;nbsp;-&amp;gt;&amp;nbsp;display();\r\n}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','2017-02-24',4,11,1),(15,'解决UE行号不对齐的问题','&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;若代码中行过长，UE会换行显示，但此时行号不会进行相应的改变，导致行号和行无法对应的问题。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;img src=&quot;/upload/image/20170224/1487938386111139.png&quot; title=&quot;1487938386111139.png&quot; alt=&quot;20.PNG&quot;/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;解决办法是前端文件中引入JQUERY并加入以下代码：&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:html;toolbar:false;&quot;&gt;&amp;lt;script&amp;gt;\r\n$(function(){\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;SyntaxHighlighter.highlight();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$(&amp;quot;table.syntaxhighlighter&amp;quot;).each(function&amp;nbsp;()&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if&amp;nbsp;(!$(this).hasClass(&amp;quot;nogutter&amp;quot;))&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;var&amp;nbsp;$gutter&amp;nbsp;=&amp;nbsp;$($(this).find(&amp;quot;.gutter&amp;quot;)[0]);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;var&amp;nbsp;$codeLines&amp;nbsp;=&amp;nbsp;$($(this).find(&amp;quot;.code&amp;nbsp;.line&amp;quot;));\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$gutter.find(&amp;quot;.line&amp;quot;).each(function&amp;nbsp;(i)&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$(this).height($($codeLines[i]).height());\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$($codeLines[i]).height($($codeLines[i]).height());\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;});\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;});\r\n});\r\n&amp;lt;/script&amp;gt;&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;效果如下：&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;img src=&quot;/upload/image/20170224/1487938581481487.png&quot; title=&quot;1487938581481487.png&quot; alt=&quot;blob.png&quot;/&gt;&lt;/span&gt;&lt;/p&gt;','2017-02-24',3,11,1),(16,'控制器中添加栏目方法的实现','&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;//&amp;nbsp;对外被访问方法&amp;quot;catadd&amp;quot;\r\npublic&amp;nbsp;function&amp;nbsp;catadd(){\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;判断有无POST数据\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if(IS_POST)&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;提交数据\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;获得数据\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$name&amp;nbsp;=&amp;nbsp;I(&amp;#39;post.catname&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;判断&amp;quot;$name&amp;quot;是否为空\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if($name)&amp;nbsp;{&amp;nbsp;//&amp;nbsp;不为空\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;整理数据\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$data[&amp;#39;name&amp;#39;]&amp;nbsp;=&amp;nbsp;$name;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$data[&amp;#39;art_ids&amp;#39;]&amp;nbsp;=&amp;nbsp;&amp;#39;&amp;#39;;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;实例化模型\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$model&amp;nbsp;=&amp;nbsp;M(&amp;#39;cat&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;提交数据\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$result&amp;nbsp;=&amp;nbsp;$model&amp;nbsp;-&amp;gt;&amp;nbsp;data($data)&amp;nbsp;-&amp;gt;&amp;nbsp;add();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if($result)&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;成功\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this&amp;nbsp;-&amp;gt;&amp;nbsp;success(&amp;#39;添加成功&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;失败\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this&amp;nbsp;-&amp;gt;&amp;nbsp;error(&amp;#39;添加失败&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;为空\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;提示为空\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this&amp;nbsp;-&amp;gt;&amp;nbsp;error(&amp;#39;栏目名称不能为空&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;默认显示\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;展示模板\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this&amp;nbsp;-&amp;gt;&amp;nbsp;display();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','2017-02-24',4,14,1),(17,'控制器中退出登录方法的实现','&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;//&amp;nbsp;对外被访问方法&amp;quot;logout&amp;quot;\r\npublic&amp;nbsp;function&amp;nbsp;logout(){\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;清空&amp;quot;$_SESSION&amp;quot;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;session(null);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;&amp;quot;$_SESSION&amp;quot;为空则跳转\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if(empty($_SESSION))&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;跳转\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this&amp;nbsp;-&amp;gt;&amp;nbsp;success(&amp;#39;成功退出&amp;#39;,U(&amp;#39;Public/index&amp;#39;));\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','2017-02-24',4,8,1),(18,'模型中自动验证评论数据','&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;新建一个文件，并写入以下内容（当前位置：网站根目录）&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;pre class=&quot;brush:bash;toolbar:false&quot;&gt;vi&amp;nbsp;/Application/Blog/Model/Ans&lt;/pre&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;lt;?php\r\n//&amp;nbsp;声明命名空间\r\nnamespace&amp;nbsp;Blog\\Model;\r\n//&amp;nbsp;引入父类模型\r\nuse&amp;nbsp;Think\\Model;\r\n//&amp;nbsp;声明类并继承父类模型\r\nclass&amp;nbsp;AnsModel&amp;nbsp;extends&amp;nbsp;Model{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;定义自动验证规则\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;protected&amp;nbsp;$_validate&amp;nbsp;=&amp;nbsp;array(\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;array(&amp;#39;username&amp;#39;&amp;nbsp;,&amp;#39;require&amp;#39;&amp;nbsp;&amp;nbsp;&amp;nbsp;,&amp;#39;用户名必须&amp;#39;)\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;,&amp;nbsp;array(&amp;#39;email&amp;#39;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;,&amp;#39;email&amp;#39;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;,&amp;#39;E-Mail格式错误&amp;#39;)\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;,&amp;nbsp;array(&amp;#39;comment&amp;#39;&amp;nbsp;&amp;nbsp;,&amp;#39;require&amp;#39;&amp;nbsp;&amp;nbsp;&amp;nbsp;,&amp;#39;请填写评论&amp;#39;)\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;);\r\n}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;','2017-02-25',2,12,1),(19,'防止翻墙操作','&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;新建一个公共控制器，将需要禁止翻墙操作的方法和无需禁止的方法分别放入不同控制器中，并将需要禁止翻墙操作的控制器中继承Think\\Controller控制器改为继承公共控制器。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;默认：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;lt;?php\r\n//&amp;nbsp;定义命名空间\r\nnamespace&amp;nbsp;Blog\\Controller;\r\n//&amp;nbsp;引入控制器\r\nuse&amp;nbsp;Think\\Controller;\r\n//&amp;nbsp;继承父类控制器\r\nclass&amp;nbsp;PublicController&amp;nbsp;extends&amp;nbsp;Controller&amp;nbsp;{&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;改为：&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;lt;?php\r\n//&amp;nbsp;定义命名空间\r\nnamespace&amp;nbsp;Blog\\Controller;\r\n//&amp;nbsp;引入分页类\r\nuse&amp;nbsp;Think\\Page;\r\n//&amp;nbsp;声明类并继承公共控制器\r\nclass&amp;nbsp;ManageController&amp;nbsp;extends&amp;nbsp;CommonController&amp;nbsp;{&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;公共控制器中内容如下：&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;pre class=&quot;brush:bash;toolbar:false;&quot;&gt;vi&amp;nbsp;Application/Blog/Controller/CommonController.class.php&lt;/pre&gt;&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;&amp;lt;?php\r\n//&amp;nbsp;定义命名空间\r\nnamespace&amp;nbsp;Blog\\Controller;\r\n//&amp;nbsp;加载父类控制器\r\nuse&amp;nbsp;Think\\Controller;\r\n//&amp;nbsp;声明类并继承父类控制器\r\nclass&amp;nbsp;CommonController&amp;nbsp;extends&amp;nbsp;Controller&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;thinkphp提供的构造方法\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;public&amp;nbsp;function&amp;nbsp;_initialize(){\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$is_login&amp;nbsp;=&amp;nbsp;session(&amp;#39;is_login&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if(empty($is_login))&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$url&amp;nbsp;=&amp;nbsp;U(&amp;#39;Public/login&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;echo&amp;nbsp;&amp;quot;&amp;lt;script&amp;gt;top.location.href=&amp;#39;$url&amp;#39;&amp;lt;/script&amp;gt;&amp;quot;;exit;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','2017-02-25',4,15,1),(20,'生成验证码','&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;//&amp;nbsp;提交评论\r\npublic&amp;nbsp;function&amp;nbsp;comment()&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;实例化模型\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$model&amp;nbsp;=&amp;nbsp;D(&amp;#39;Ans&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;自动验证\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if(!$model-&amp;gt;create()){\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;如果创建失败&amp;nbsp;表示验证没有通过&amp;nbsp;输出错误提示信息\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this&amp;nbsp;-&amp;gt;&amp;nbsp;error($model-&amp;gt;getError());\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;验证通过\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;入库\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$result&amp;nbsp;=&amp;nbsp;$model&amp;nbsp;-&amp;gt;&amp;nbsp;add();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if($result)&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;更新&amp;nbsp;blog_art&amp;nbsp;表中的数据\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$model&amp;nbsp;=&amp;nbsp;M();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$res&amp;nbsp;=&amp;nbsp;$model&amp;nbsp;-&amp;gt;&amp;nbsp;table(&amp;#39;blog_art&amp;nbsp;as&amp;nbsp;A,&amp;nbsp;blog_ans&amp;nbsp;as&amp;nbsp;B&amp;#39;)&amp;nbsp;-&amp;gt;&amp;nbsp;where(&amp;#39;A.id&amp;nbsp;=&amp;nbsp;&amp;#39;.I(&amp;#39;post.art_id&amp;#39;))&amp;nbsp;-&amp;gt;&amp;nbsp;setInc(&amp;#39;A.ans_number&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if($res)&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this&amp;nbsp;-&amp;gt;&amp;nbsp;success(&amp;#39;评论成功&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this&amp;nbsp;-&amp;gt;&amp;nbsp;error(&amp;#39;更新评论数失败&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this&amp;nbsp;-&amp;gt;&amp;nbsp;error(&amp;#39;评论失败&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n}&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','2017-02-25',4,9,1),(21,'文章内容展示方法','&lt;pre class=&quot;brush:php;toolbar:false&quot;&gt;//&amp;nbsp;对外访问方法&amp;quot;art&amp;quot;\r\npublic&amp;nbsp;function&amp;nbsp;art(){\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;保存&amp;quot;$id&amp;quot;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$id&amp;nbsp;=&amp;nbsp;I(&amp;#39;get.id&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if(!$id)&amp;nbsp;{&amp;nbsp;&amp;nbsp;//&amp;nbsp;若&amp;quot;$id&amp;quot;为空，则报错\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this&amp;nbsp;-&amp;gt;&amp;nbsp;error(&amp;#39;无效ID&amp;#39;,U(&amp;#39;Public/index&amp;#39;));\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;否则就往下执行\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;实例化模型\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$model&amp;nbsp;=&amp;n