mp;nbsp;=&amp;nbsp;array(&amp;#39;like&amp;#39;,&amp;nbsp;&amp;#39;%&amp;#39;.$title.&amp;#39;%&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$map[&amp;#39;status&amp;#39;]&amp;nbsp;=&amp;nbsp;1;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$count&amp;nbsp;=&amp;nbsp;$model&amp;nbsp;-&amp;gt;&amp;nbsp;table(&amp;#39;blog_art&amp;#39;)&amp;nbsp;-&amp;gt;&amp;nbsp;where($map)&amp;nbsp;-&amp;gt;&amp;nbsp;count();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;&amp;nbsp;按&amp;quot;id&amp;quot;展示数据\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;计算总条数\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$count&amp;nbsp;=&amp;nbsp;$model&amp;nbsp;-&amp;gt;&amp;nbsp;table(&amp;#39;blog_art&amp;#39;)&amp;nbsp;-&amp;gt;&amp;nbsp;where(&amp;#39;status=1&amp;#39;)&amp;nbsp;-&amp;gt;&amp;nbsp;count();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;实例化分页类\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$page&amp;nbsp;=&amp;nbsp;new&amp;nbsp;\\Think\\Page($count,&amp;nbsp;3);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;分页显示输出\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$show&amp;nbsp;=&amp;nbsp;$page&amp;nbsp;-&amp;gt;&amp;nbsp;show();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;查询结果\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if($catid)&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;若GET到&amp;quot;catid&amp;quot;变量\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;查询相应的数据\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$result&amp;nbsp;=&amp;nbsp;$model&amp;nbsp;-&amp;gt;&amp;nbsp;field(&amp;#39;A.id,A.cre_date,A.title,A.content,B.name,B.id&amp;nbsp;as&amp;nbsp;cid,A.ans_number&amp;#39;)&amp;nbsp;-&amp;gt;&amp;nbsp;table(&amp;#39;blog_art&amp;nbsp;as&amp;nbsp;A,blog_cat&amp;nbsp;as&amp;nbsp;B&amp;#39;)&amp;nbsp;-&amp;gt;&amp;nbsp;where(&amp;#39;A.cat_id&amp;nbsp;=&amp;nbsp;B.id&amp;nbsp;and&amp;nbsp;A.cat_id=&amp;#39;.$catid.&amp;#39;&amp;nbsp;and&amp;nbsp;A.status=1&amp;#39;)&amp;nbsp;-&amp;gt;&amp;nbsp;limit($page-&amp;gt;firstRow.&amp;#39;,&amp;#39;.$page-&amp;gt;listRows)&amp;nbsp;-&amp;gt;&amp;nbsp;order(&amp;#39;A.id&amp;nbsp;desc&amp;#39;)&amp;nbsp;-&amp;gt;&amp;nbsp;select();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;if($title)&amp;nbsp;{&amp;nbsp;//&amp;nbsp;若GET到&amp;quot;title&amp;quot;变量\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;查询符合的数据\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$map[&amp;#39;title&amp;#39;]&amp;nbsp;=&amp;nbsp;array(&amp;#39;like&amp;#39;,&amp;nbsp;&amp;#39;%&amp;#39;.$title.&amp;#39;%&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$map[&amp;#39;status&amp;#39;]&amp;nbsp;=&amp;nbsp;1;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$result&amp;nbsp;=&amp;nbsp;$model&amp;nbsp;-&amp;gt;&amp;nbsp;table(&amp;#39;blog_art&amp;#39;)&amp;nbsp;-&amp;gt;&amp;nbsp;where($map)&amp;nbsp;-&amp;gt;&amp;nbsp;limit($page-&amp;gt;firstRow.&amp;#39;,&amp;#39;.$page-&amp;gt;listRows)&amp;nbsp;-&amp;gt;&amp;nbsp;order(&amp;#39;id&amp;nbsp;desc&amp;#39;)&amp;nbsp;-&amp;gt;&amp;nbsp;select();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;若无以上变量\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;查询相应的数据\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$result&amp;nbsp;=&amp;nbsp;$model&amp;nbsp;-&amp;gt;&amp;nbsp;field(&amp;#39;A.id,A.cre_date,A.title,A.content,B.name,B.id&amp;nbsp;as&amp;nbsp;cid,A.ans_number&amp;#39;)&amp;nbsp;-&amp;gt;&amp;nbsp;table(&amp;#39;blog_art&amp;nbsp;as&amp;nbsp;A,blog_cat&amp;nbsp;as&amp;nbsp;B&amp;#39;)&amp;nbsp;-&amp;gt;&amp;nbsp;where(&amp;#39;A.cat_id&amp;nbsp;=&amp;nbsp;B.id&amp;nbsp;and&amp;nbsp;A.status=1&amp;#39;)&amp;nbsp;-&amp;gt;&amp;nbsp;limit($page-&amp;gt;firstRow.&amp;#39;,&amp;#39;.$page-&amp;gt;listRows)&amp;nbsp;-&amp;gt;&amp;nbsp;order(&amp;#39;A.id&amp;nbsp;desc&amp;#39;)&amp;nbsp;-&amp;gt;&amp;nbsp;select();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;if($title)&amp;nbsp;{&amp;nbsp;//&amp;nbsp;若GET到&amp;quot;title&amp;quot;变量\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;查询符合的数据\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$map[&amp;#39;title&amp;#39;]&amp;nbsp;=&amp;nbsp;array(&amp;#39;like&amp;#39;,&amp;nbsp;&amp;#39;%&amp;#39;.$title.&amp;#39;%&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$map[&amp;#39;status&amp;#39;]&amp;nbsp;=&amp;nbsp;1;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$result&amp;nbsp;=&amp;nbsp;$model&amp;nbsp;-&amp;gt;&amp;nbsp;table(&amp;#39;blog_art&amp;#39;)&amp;nbsp;-&amp;gt;&amp;nbsp;where($map)&amp;nbsp;-&amp;gt;&amp;nbsp;limit($page-&amp;gt;firstRow.&amp;#39;,&amp;#39;.$page-&amp;gt;listRows)&amp;nbsp;-&amp;gt;&amp;nbsp;order(&amp;#39;id&amp;nbsp;desc&amp;#39;)&amp;nbsp;-&amp;gt;&amp;nbsp;select();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}&amp;nbsp;else&amp;nbsp;{&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;若无以上变量\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;查询相应的数据\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$result&amp;nbsp;=&amp;nbsp;$model&amp;nbsp;-&amp;gt;&amp;nbsp;field(&amp;#39;A.id,A.cre_date,A.title,A.content,B.name,B.id&amp;nbsp;as&amp;nbsp;cid,A.ans_number&amp;#39;)&amp;nbsp;-&amp;gt;&amp;nbsp;table(&amp;#39;blog_art&amp;nbsp;as&amp;nbsp;A,blog_cat&amp;nbsp;as&amp;nbsp;B&amp;#39;)&amp;nbsp;-&amp;gt;&amp;nbsp;where(&amp;#39;A.cat_id&amp;nbsp;=&amp;nbsp;B.id&amp;nbsp;and&amp;nbsp;A.status=1&amp;#39;)&amp;nbsp;-&amp;gt;&amp;nbsp;limit($page-&amp;gt;firstRow.&amp;#39;,&amp;#39;.$page-&amp;gt;listRows)&amp;nbsp;-&amp;gt;&amp;nbsp;order(&amp;#39;A.id&amp;nbsp;desc&amp;#39;)&amp;nbsp;-&amp;gt;&amp;nbsp;select();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;分割内容字符串\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$length&amp;nbsp;=&amp;nbsp;count($result);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;for($i=0;&amp;nbsp;$i&amp;lt;$length;&amp;nbsp;$i++)&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$temp&amp;nbsp;=&amp;nbsp;mb_substr($result[$i][&amp;#39;content&amp;#39;],0,500,&amp;#39;utf-8&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if($temp&amp;nbsp;!=&amp;nbsp;$result[$i][&amp;#39;content&amp;#39;])&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$temp&amp;nbsp;.=&amp;nbsp;&amp;#39;...&amp;#39;;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$result[$i][&amp;#39;content&amp;#39;]&amp;nbsp;=&amp;nbsp;$temp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;获得导航栏\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$line&amp;nbsp;=&amp;nbsp;$model&amp;nbsp;-&amp;gt;&amp;nbsp;field(&amp;#39;name,id&amp;#39;)&amp;nbsp;-&amp;gt;&amp;nbsp;table(&amp;#39;blog_cat&amp;#39;)&amp;nbsp;-&amp;gt;&amp;nbsp;select();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;获得标题列表\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$list&amp;nbsp;=&amp;nbsp;$model&amp;nbsp;-&amp;gt;&amp;nbsp;field(&amp;#39;name,&amp;nbsp;id&amp;#39;)&amp;nbsp;-&amp;gt;&amp;nbsp;table(&amp;#39;blog_cat&amp;#39;)&amp;nbsp;-&amp;gt;&amp;nbsp;select();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$len&amp;nbsp;=&amp;nbsp;count($list);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;for($i=0;&amp;nbsp;$i&amp;lt;$len;&amp;nbsp;$i++)&amp;nbsp;{\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$list[$i][&amp;#39;list&amp;#39;]&amp;nbsp;=&amp;nbsp;$model&amp;nbsp;-&amp;gt;&amp;nbsp;field(&amp;#39;title,id,ans_number&amp;#39;)&amp;nbsp;-&amp;gt;&amp;nbsp;table(&amp;#39;blog_art&amp;#39;)&amp;nbsp;-&amp;gt;&amp;nbsp;where(&amp;#39;status=1&amp;nbsp;and&amp;nbsp;cat_id=&amp;#39;.$list[$i][&amp;#39;id&amp;#39;])&amp;nbsp;-&amp;gt;&amp;nbsp;select();\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;限定字串长度\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$length&amp;nbsp;=&amp;nbsp;count($list[$i][&amp;#39;list&amp;#39;]);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;for($j=0;&amp;nbsp;$j&amp;lt;$length;&amp;nbsp;$j++){\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$temp&amp;nbsp;=&amp;nbsp;mb_substr($list[$i][&amp;#39;list&amp;#39;][$j][&amp;#39;title&amp;#39;],0,10,&amp;#39;utf-8&amp;#39;);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;if($temp&amp;nbsp;!=&amp;nbsp;$list[$i][&amp;#39;list&amp;#39;][$j][&amp;#39;title&amp;#39;])\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$temp&amp;nbsp;.=&amp;nbsp;&amp;#39;...&amp;#39;;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$list[$i][&amp;#39;list&amp;#39;][$j][&amp;#39;title&amp;#39;]&amp;nbsp;=&amp;nbsp;$temp;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;}\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;//&amp;nbsp;分配模板变量\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this&amp;nbsp;-&amp;gt;&amp;nbsp;assign(&amp;#39;list&amp;#39;,&amp;nbsp;$list);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this&amp;nbsp;-&amp;gt;&amp;nbsp;assign(&amp;#39;line&amp;#39;,&amp;nbsp;$line);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this&amp;nbsp;-&amp;gt;&amp;nbsp;assign(&amp;#39;data&amp;#39;,&amp;nbsp;$result);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this&amp;nbsp;-&amp;gt;&amp;nbsp;assign(&amp;#39;pages&amp;#39;,&amp;nbsp;$show);\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;$this&amp;nbsp;-&amp;gt;&amp;nbsp;display();&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;','2017-02-25',4,44,1),(23,'我对贵公司面试题的理解','&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;　　您好，我先简单地自我介绍一下：姚斌，男，92年生。我在大学（2012-2016）期间，曾&lt;strong&gt;自学&lt;/strong&gt;过 PHP、C 和 Linux 的相关知识，这个博客是我用Thinkphp写的，主要用来找工作。有学习的动力，毅力。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;　　我看了贵公司的PHP面试题，感觉正是我为之努力奋斗的目标。下面是我对其中一些的解答、和看法。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family:微软雅黑, Microsoft YaHei&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;h4&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;　　１、Git 使用程度：&lt;/span&gt;&lt;/h4&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;　　我选&amp;nbsp;&lt;strong&gt;C 、偶尔&lt;/strong&gt;。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;　　本人曾经使用过“zsh”这款shell，目的自然是其美观、易于使用，而其大名鼎鼎的“oh my zsh”插件令我眼前一亮，并深深地喜欢上了，它默认就是使用GIT安装的。不过可惜的是，我没能深入地去学习，去了解github。然在您的点拨下，我决定写完这篇文章就去学习它。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;h4&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;　　2、github相关：&lt;/span&gt;&lt;/h4&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;　　我选&lt;strong&gt;B、有账号只使用研究&lt;/strong&gt;。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;　　我的账号是“yb3616”，这个账号是多年前注册的，为了和开源作者讨论一些问题（web端）。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;h4&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;　　３、PHP的框架，掌握程度：&lt;/span&gt;&lt;/h4&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;正如您所见，本博客系统就是使用国人开发的MVC框架“Thinkphp”进行开发的，前端模板使用的是“燕十八”老师的模板，后端是我根据自己所知，参考Thinkphp开发手册实现的所有功能。麻雀虽小，五脏俱全，希望能得到您的认可。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;h4&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;　　４、PHP7的新特性有哪些：&lt;/span&gt;&lt;/h4&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;　　这个我在它还没出来的时候，就从网上听说它的性能怎么怎么好，是php5的2倍。自从它一出来，我开始就用了。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;　　先澄清一下，这个博客所在的VPS我没有使用php7，而是使用php5，本来这款VPS是我科学上网用的，稳定优先。最近写了个博客，就业挂在上面了。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;　　但是我有台树莓派2的开发板，一直拿来做linux服务器，系统用的是ArchLinux，它上面运行的是php7。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;　　其他的就是mysql_函数被弃用了，而PDO支持连接多款数据库，是更好的替换产品。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;　　还有内存的改善。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;　　以上是我平时上网零星了解的内容，看了您的笔试题后，我也Google到了php官方的回答：&lt;/span&gt;&lt;a href=&quot;http://php.net/manual/zh/migration70.new-features.php&quot; style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;http://php.net/manual/zh/migration70.new-features.php&lt;/a&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;　　我想您应该知道这些，就不一一粘贴下来浪费您的时间。&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;&lt;h4&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;　　５、写一条sql语句&lt;/span&gt;&lt;/h4&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;用户表结构\r\nCREATE&amp;nbsp;TABLE&amp;nbsp;`user`&amp;nbsp;(\r\n&amp;nbsp;&amp;nbsp;`id`&amp;nbsp;int(11)&amp;nbsp;unsigned&amp;nbsp;NOT&amp;nbsp;NULL&amp;nbsp;AUTO_INCREMENT,\r\n&amp;nbsp;&amp;nbsp;`name`&amp;nbsp;char(11)&amp;nbsp;NOT&amp;nbsp;NULL&amp;nbsp;COMMENT&amp;nbsp;&amp;#39;账号&amp;#39;,\r\n&amp;nbsp;&amp;nbsp;`password`&amp;nbsp;char(64)&amp;nbsp;NOT&amp;nbsp;NULL&amp;nbsp;COMMENT&amp;nbsp;&amp;#39;密码&amp;#39;,\r\n&amp;nbsp;&amp;nbsp;`create_at`&amp;nbsp;int(11)&amp;nbsp;NOT&amp;nbsp;NULL&amp;nbsp;COMMENT&amp;nbsp;&amp;#39;创建时间&amp;#39;,\r\n&amp;nbsp;&amp;nbsp;PRIMARY&amp;nbsp;KEY&amp;nbsp;(`id`)\r\n)&amp;nbsp;ENGINE=InnoDB&amp;nbsp;DEFAULT&amp;nbsp;CHARSET=utf8&amp;nbsp;COMMENT=&amp;#39;用户&amp;#39;&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;查询手机 13012341234 是否存在的mysql语句&lt;/span&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;答：\r\nSELECT&amp;nbsp;`id`&amp;nbsp;FROM&amp;nbsp;`user`&amp;nbsp;where&amp;nbsp;`name`&amp;nbsp;=&amp;nbsp;&amp;#39;13012341234&amp;#39;&amp;nbsp;LIMIT&amp;nbsp;1;&lt;/pre&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;　　我不大明白您的出题意图，但还是说下我的回答：id字段虽然您标了int(11)，正好符合手机号长度，但基于三点&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;ol class=&quot; list-paddingleft-2&quot; style=&quot;list-style-type: decimal;&quot;&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;实际使用中id字段不会赋予真实内容&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;id&amp;nbsp;字段的自动增长和主键更让我确定以上想法&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;实际使用中会把手机号设置成字符串型，便于按区位搜索，又因为其固定长度，所以往往会被设置成char(11)。&lt;/span&gt;&lt;/p&gt;&lt;/li&gt;&lt;/ol&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;综上，我更认为手机号存于name字段，虽然也有不合理之处，但这是最好的解释了。&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;h4 style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;6、因为数据增长过快，比如现在user表已经超过100万的数据，查询最新注册的10个人账号的mysql语句&lt;/span&gt;&lt;/h4&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;SELECT&amp;nbsp;*&amp;nbsp;FROM&amp;nbsp;`user`&amp;nbsp;ORDER&amp;nbsp;BY&amp;nbsp;ID&amp;nbsp;DESC&amp;nbsp;LIMIT&amp;nbsp;0,10;&lt;/pre&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;上面是利用了id的自动增长属性，因为id是主建，性能更好。不在乎性能的话可以这么来&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;SELECT&amp;nbsp;*&amp;nbsp;FROM&amp;nbsp;`user`&amp;nbsp;ORDER&amp;nbsp;BY&amp;nbsp;`create_at`&amp;nbsp;DESC&amp;nbsp;LIMIT&amp;nbsp;0,10;&lt;/pre&gt;&lt;h4&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/h4&gt;&lt;h4 style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;7、Linux使用程度&lt;/span&gt;&lt;/h4&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;我选&lt;strong&gt;B、基本会，掌握常见命令&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;如果C选项没有做过运维这一条的话，我就会选C，常见的Linux发行版好像就gentoo没用过，曾经也不自量力地用virtualbox去编译lfs， 最后卡的我的I3不要不要的。&lt;strong&gt;&lt;br/&gt;&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;VPS用的是centos6，树莓派用的是Archlinux。常见的命令都熟悉，不常见的命令也可能用过。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;h4 style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;8、自己的硬件设备，比如电脑、手机是什么牌子的？&lt;/span&gt;&lt;/h4&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;笔记本用的是13年买的Thinkpad X230i，我就是那时候接触的各类编程。自己加过内存条和固态硬盘，现在用着还算流畅。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;手机用的是小米5高配。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;如果还算的话，我还有俩树莓派2，和一块1T人西部蓝盘，连在树莓派上跑Samba，aria2，和lnmp环境（当时应该买红盘的，用不到蓝盘这么好的性能，关键是太吵）。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;/p&gt;&lt;h4 style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;9、面对技术成长迷茫，为什么迷茫？怎么处理?&lt;/span&gt;&lt;/h4&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;没人带，没人要，我感觉技术还可以，发的简历就是没人通过。做了几天仿站，我深信这不是我要走的路，后来临近毕业，回家后又忙着考驾照，考完驾照都11月份了，年末又不好找工作，一直拖到现在还没工作。家里人明着暗着催了我几次了，其实我也急，但是工作真的不好找，欸。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;br/&gt;&lt;/p&gt;&lt;h4 style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;最后&lt;/span&gt;&lt;/h4&gt;&lt;p style=&quot;text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;;&quot;&gt;我对贵公司120%的满意，如果您觉得我的能力勉强符合你们的要求的话，请给我一个机会，我不能保证单独按时完成中大型网站，但是我愿用120%的努力去学习，去完成你们交给我的任务。谢谢！&lt;/span&gt;&lt;/p&gt;','2017-02-27',1,0,1);
